import curses
import threading
import time

class GlobalClock:
    def __init__(self, interval, callback):
        self.interval = interval
        self.callback = callback
        self.running = False
        self.paused = False
        self.thread = None
        self.lock = threading.Lock()
        self.condition = threading.Condition(self.lock)
    def start(self):
        if not self.running:
            self.running = True
            self.thread = threading.Thread(target=self._run)
            self.thread.start()
    def _run(self):
        while self.running:
            with self.condition:
                if self.paused:
                    self.condition.wait()
                if not self.running:
                    break
            time.sleep(self.interval)
            self.callback()
    def play_pause(self):
        with self.condition:
            self.paused = not self.paused
            if not self.paused:
                self.condition.notify()        
    def stop(self):
        with self.condition:
            self.running = False
            self.paused = False
            self.condition.notify()
        self.thread.join()

def addstr_mid(window, string, height=0, color_pair=0):
    win_height, win_width = window.getmaxyx()
    window.addstr(height, win_width//2 - len(string)//2, string, curses.color_pair(color_pair))

def color(string):
    string = string.lower()
    if string=="red":
        return 1
    elif string=="green":
        return 2
    elif string=="yellow":
        return 3
    elif string=="blue":
        return 4
    elif string=="magenta":
        return 5
    elif string=="cyan":
        return 6
    elif string=='white':
        return 0
    
def format_frequency(frequency):
    prefixes = ['', 'K', 'M', 'G'] 
    magnitude = 0
    while frequency >= 1000:
        frequency /= 1000
        magnitude += 1
    formatted_frequency = "{:.0f}{}".format(frequency, prefixes[magnitude] + 'Hz')
    return formatted_frequency

