    curses.init_pair(2, curses.COLOR_GREEN, curses.COLOR_BLACK)
    curses.init_pair(3, curses.COLOR_YELLOW, curses.COLOR_BLACK)
    curses.init_pair(4, curses.COLOR_BLUE, curses.COLOR_BLACK)
    curses.init_pair(5, curses.COLOR_MAGENTA, curses.COLOR_BLACK)
    curses.init_pair(6, curses.COLOR_CYAN, curses.COLOR_BLACK) 

    color_pairs = [1, 2, 3, 4, 5, 6]
    color_idx = [0]

    clock_state = [False]
    freq = 20

    def clock_tick():
        global extMemData
        global extMemState
        global RGData
        global RGState
        global ALUData
        global ALUState
        global MRData
        global MRState
        global PCData
        global PCState
        global SPData
        global SPState
        global ORData
        global ORState
        global ARData
        global ARState
        global IRData
        global IRState
        global MPSeqData
        global MPSeqState
        global MPMemData

        # color_idx[0] = (color_idx[0] + 1) % len(color_pairs)
        clock_state[0] = clock_state[0]^True
        if clock_state[0]: # rising
            update_clock(clk, freq, color("yellow"))
        else: # falling
            update_clock(clk, freq, color("white"))
            IRData = (IRData+1)%0xFF
            MPSeqData = encode_IRtoMPS(IRData)
            MPMemData = encode_MPStoCSSig(MPSeqData)
            update_IRData(i_r)
            update_MPSeqData(mps)
            update_MPMemData(mp_mem)


    clock = GlobalClock(1/freq, clock_tick)

    # ----------STATIC ELEMENTS----------

    # title
    stdscr.box()
    addstr_mid(stdscr, " DSM Processor Simulator ")
    stdscr.refresh()

    # clock window
    height = 3; width = 11
    begin_x = 4; begin_y = 2
    clk = curses.newwin(height, width, begin_y, begin_x)
    clk.box()
    addstr_mid(clk, " Clock ", color_pair=3)
    clk.refresh()

    # external memory window
    height = 5; width = 60
    begin_x = main_mid - width//2; begin_y = 2
